import React, {useState, useEffect} from 'react'
import {
    StyleSheet,
    Text,
    View,
    Image,
    TextInput,
    Button,
    TouchableOpacity,
  } from "react-native";

import axios from 'axios';
import { ScrollView } from 'react-native-gesture-handler';

const api_url = "http://parkly-env.eba-u2qumtf7.us-east-2.elasticbeanstalk.com";

const DetailsPage = ({navigation, route}) => {

    const [name, setName] = useState(route.params.item.name);
    const [country, setCountry] = useState(route.params.item.address.country);
    const [town, setTown] = useState(route.params.item.address.town);
    const [streetName, setStreetName] = useState(route.params.item.address.streetName);
    const [streetNumber, setStreetNumber] = useState(route.params.item.address.streetNumber);
    const [spotsTotal, setSpotsTotal] = useState(route.params.item.spotsTotal);
    const [firstName, setFirstName] = useState(route.params.item.firstName);
    const [lastName, setLastName] = useState(route.params.item.lastName);
    const [companyName, setCompanyName] = useState(route.params.item.companyName);
    const [ownerCountry, setOwnerCountry] = useState(route.params.item.ownerCountry);
    const [ownerTown, setOwnerTown] = useState(route.params.item.ownerTown);
    const [ownerStreet, setOwnerStreet] = useState(route.params.item.ownerStreet);
    const [ownerStreetNumber, setOwnerStreetNumber] = useState(route.params.item.ownerStreetNumber);

    const [isEditingAddress, setAddressEditing] = useState(false);
    const [isInvoiceEditing, setInvoiceEditing] = useState(false);
    const [err, setErr] = useState("");
    
    const options = {
        headers: {'security-token': route.params.token}
    }

    const updateParking = () => {
        axios.put(`${api_url}/p/parkings/${route.params.item.id}?createNewOwner=false`, {
            name: name,
            address: {
                country: country,
                town: town,
                streetName: streetName,
                streetNumber: streetNumber
            },
            spotsTotal: spotsTotal,
            parkingOwner: {
                firstName: firstName,
                lastName: lastName,
                companyName: companyName,
                address: {
                    country: ownerCountry,
                    town: ownerTown,
                    streetName: ownerStreet,
                    streetNumber: ownerStreetNumber
                }
            }
        },
        options).then((response) => navigation.navigate('List', {token: route.params.token, user: route.params.username})).catch(err => setErr(err))
    }

    const deleteParking = () => {
        axios.delete(`${api_url}/p/parkings/${route.params.item.id}`, 
        options).then((response) => navigation.navigate('List', {token: route.params.token, user: route.params.username})).catch(err => setErr(err))
    }

    return (
        <View style={styles.container}>

            <ScrollView>
                <View style={styles.actionBar} >
                    <Text>Address</Text>
                    {isEditingAddress == false ? 
                    <TouchableOpacity onPress={() => setAddressEditing(true)}><Text>Edit</Text></TouchableOpacity>
                    :
                    <TouchableOpacity onPress={() => setAddressEditing(false)}><Text>Save</Text></TouchableOpacity>
                    }
                </View>

                <View>
                    {isEditingAddress == false ? 
                    <Text>{country}</Text>
                    :
                    <TextInput value={country} onChangeText={(val) => setCountry(val)}></TextInput>}
                </View>
                <View>
                    {isEditingAddress == false ? 
                    <Text>{streetName}</Text>
                    :
                    <TextInput value={streetName} onChangeText={(val) => setStreetName(val)}></TextInput>}
                </View>
                <View>
                    {isEditingAddress == false ? 
                    <Text>{streetNumber}</Text>
                    :
                    <TextInput value={streetNumber} onChangeText={(val) => setStreetNumber(val)}></TextInput>}
                </View>
                <View>
                    {isEditingAddress == false ? 
                    <Text>{town}</Text>
                    :
                    <TextInput value={town} onChangeText={(val) => setTown(val)}></TextInput>}
                </View>
                
                <View style={styles.actionBar} >
                    <Text>Invoice Information</Text>
                    {isInvoiceEditing == false ? 
                    <TouchableOpacity onPress={() => setInvoiceEditing(true)}><Text>Edit</Text></TouchableOpacity>
                    :
                    <TouchableOpacity onPress={() => setInvoiceEditing(false)}><Text>Save</Text></TouchableOpacity>
                    }
                </View>

                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{firstName}</Text>
                    :
                    <TextInput value={firstName} onChangeText={(val) => setFirstName(val)}></TextInput>}
                </View>
                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{lastName}</Text>
                    :
                    <TextInput value={lastName} onChangeText={(val) => setLastName(val)}></TextInput>}
                </View>
                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{ownerCountry}</Text>
                    :
                    <TextInput value={ownerCountry} onChangeText={(val) => setOwnerCountry(val)}></TextInput>}
                </View>
                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{ownerStreet}</Text>
                    :
                    <TextInput value={ownerStreet} onChangeText={(val) => setOwnerStreet(val)}></TextInput>}
                </View>
                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{ownerStreetNumber}</Text>
                    :
                    <TextInput value={ownerStreetNumber} onChangeText={(val) => setOwnerStreetNumber(val)}></TextInput>}
                </View>
                <View>
                    {isInvoiceEditing == false ? 
                    <Text>{ownerTown}</Text>
                    :
                    <TextInput value={ownerTown} onChangeText={(val) => setOwnerTown(val)}></TextInput>}
                </View>


            </ScrollView>
            <View style={styles.buttonHolder}>
                <TouchableOpacity onPress={updateParking} style={styles.actionButton}>
                    <Text>Update</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={deleteParking} style={styles.actionButton}>
                    <Text>Delete</Text>
                </TouchableOpacity>
            </View>
            

        </View>
    )

} 

const styles = StyleSheet.create({
    
    container: {
        flex: 1,
        backgroundColor: "#fff",
    },

    buttonHolder: 
    {
        flex: 1,
        flexDirection: "row"
    },

    actionBar: {
        backgroundColor: "#ffd300"
    },

    actionButton: {
        borderRadius: 20, 
        backgroundColor: "yellow", 
        height: 40, 
        width: 100, 
        margin: 35
    }
});

export default DetailsPage;
